import base64
import binascii

# A chave pública em formato hexadecimal
hex_key = ""

# Converter hexadecimal para binário
binary_key = binascii.unhexlify(hex_key)

# Converter binário para Base64
base64_key = base64.b64encode(binary_key).decode("ascii")

# Formatar para PEM
pem_key = f"-----BEGIN PUBLIC KEY-----\n{base64_key}\n-----END PUBLIC KEY-----"

# Escrever a chave em um arquivo PEM
with open("public_key.pem", "w") as pem_file:
    pem_file.write(pem_key)

print("Chave pública em formato PEM gerada e salva como 'public_key.pem'.")
